import React, {useState} from 'react';
import moment from 'moment';

function TodoForm(input) {

    const [todo, setTodo] = useState('');
    const [todoList, setTodoList] = useState([]);

    function handleChange(e) {
        setTodo(e.target.value);
    };

    function addTask() {
        if (todo != "") {
            const taskDetails = {
                id: Math.floor(Math.random() * 1000),
                //title: title,
                task: todo,
                isCompleted: false,
                dateCreated: moment().format('MMMM Do YYYY, h:mm:ss a')
            }

            setTodoList([...todoList, taskDetails]);
        }
    };

    function deleteTask(e, id) {

        e.preventDefault();
        setTodoList(todoList.filter(task => task.id != id));   
    };

    return (
        <form className="form" >
            <input 
                type="textarea" 
                name="text" 
                id="text" 
                placeholder="Enter task..."
                onChange={(e) => handleChange(e)} >
            </input>
            <button className="add-btn" onClick={addTask}>Add</button>
            <br/>

            {todoList != [] ? (
            <ul>
                {todoList.map(task => 
                <li style={{
                    width: "400px",
                    display: "flex",
                    justifyContent: "space-between",
                    listStyleType: "none",
                    padding: "10px",
                    textAlign: "start",
                    border: "1px solid grey",
                    backgroundColor: "rgba(50, 50, 50, 0.5)", 
                    color: "white"
                }}> {task.task} {task.dateCreated}
                <button onClick={(e) => deleteTask(e, task.id)} 
                style={{
                    width: "80px",
                    backgroundColor: "red",
                    color: "white",
                    border: "1px solid red",
                    borderRadius: "20px",
                    textAlign: "center"
                }}>Delete</button>
                  <button style={{
                    width: "80px",
                    backgroundColor: "green",
                    color: "white",
                    border: "1px solid green",
                    borderRadius: "20px",
                    textAlign: "center",
                    marginLeft: "20px"
                }}>Completed</button>
                </li>

                )}
            </ul>
            ) : null}

        </form>
    );
}


export default TodoForm;
