import React, { useState } from 'react';
import '../styles/TodoList.css';
import Modal from 'react-modal'; 

function TodoList(props) {

    const [modalIsOpen, setModalIsOpen] = useState(false);
    return (
    <div className='grid-wrapper'>
    {props.todoList !== [] ? (
    <ul>
        {props.todoList.map(task => 
        <li className='grid-item' key={task.id} onClick={() => setModalIsOpen(true)} >  
        <Modal isOpen={modalIsOpen} onRequestClose={() => setModalIsOpen(false)}>
                <div className='modal'>
                    <span className='note-date'>{task.dateCreated}</span><br/>
                    <span className='note-title'> <strong>Title : {task.title}</strong></span>
                    <span> <b><u>Text</u></b> : {task.task}</span>                
                    <button onClick={() => setModalIsOpen(false)}>Close</button>
                </div>

        </Modal>
            <div className='note-wrapper'>
                <span className='note-date'>{task.dateCreated}</span>
                <span className='note-title'> <strong>Title : {task.title}</strong></span>
                <span> <b><u>Text</u></b> : {task.task}</span>
            </div>
            <div className='btn-wrapper'>
                <button className='btn-red' onClick={(e) => props.deleteTask(e, task.id)}>Delete</button>                                                         
                <button className='btn-green'>Completed</button>
            </div>
        </li>

        )}
    </ul>
    ) : null}
</div>
    );
}

export default TodoList;
