import React, {useState} from 'react';
import TodoForm from './TodoForm';
import Todo from './Todo';

function TodoList() {
    return(
        <div>
                {todoList != [] ? (
                <ul>
                    {todoList.map(task => 
                    <li style={{
                        width: "400px",
                        display: "flex",
                        justifyContent: "space-between",
                        listStyleType: "none",
                        padding: "10px",
                        textAlign: "start",
                        border: "1px solid grey",
                        backgroundColor: "rgba(50, 50, 50, 0.5)", 
                        color: "white"
                    }}> {task.task} {task.dateCreated}
                    <button onClick={deleteTask} 
                    style={{
                        width: "80px",
                        backgroundColor: "red",
                        color: "white",
                        border: "1px solid red",
                        borderRadius: "20px",
                        textAlign: "center"
                    }}>Delete</button>
                    <button style={{
                        width: "80px",
                        backgroundColor: "green",
                        color: "white",
                        border: "1px solid green",
                        borderRadius: "20px",
                        textAlign: "center",
                        marginLeft: "20px"
                    }}>Completed</button>
                    </li>

                    )}
                </ul>
                ) : null}
        </div>
    )
}

export default TodoList;
