import React, { useState } from 'react';
import '../styles/TodoList.css';
import Modal from 'react-modal';
import { EditText } from 'react-edit-text';

function TodoList(props) {
	const [modalIsOpen, setModalIsOpen] = useState(false);
	const [editText, setEditText] = useState('');
	const [taskSave, setTaskSave] = useState({
		id: '',
		title: '',
		task: '',
		isCompleted: '',
		dateCreated: '',
	});

	function handleEditText(e) {
		setEditText(e.value);
		e.stopPropagation();
	}

	function editTask(task) {
		task.task = editText;
		setModalIsOpen(false);
	}

    function openNote(task){
        setModalIsOpen(true)
        setTaskSave(task);
    }

	return (
		<div className='grid-wrapper'>
			{props.todoList !== [] ? (
				<ul>
					{props.todoList.map((task) => (
						<li className='grid-item' key={task.id} onClick={() => openNote(task)}>
							<div className='note-wrapper' onClick={() => setModalIsOpen(true)}>
								<span className='note-date'>{task.dateCreated}</span>
								<span className='note-title'> {' '} <strong><u>Title</u> : {task.title}</strong></span>
								<span className='note-text'>{' '}<b><u>Text</u></b>{' '}: {task.task}</span>			
				            </div>
							<div className='btn-wrapper'>
								<button className='btn-red' onClick={(e) => props.deleteTask(e, task.id)}>Delete</button>
							</div>
						</li>
					))}
				</ul>
			) : null}

				<Modal
					isOpen={modalIsOpen}
					shouldCloseOnOverlayClick={false}
					onRequestClose={() => setModalIsOpen(false)}>
					<div className='modal'>
						<div className='note-date'>{taskSave.dateCreated}</div>
						<br />
						<div className='note-title'>
							{' '}
							<strong>Title : {taskSave.title}</strong>
						</div>
						<EditText onSave={(e) => handleEditText(e)} defaultValue={taskSave.task} />
						<div>
							<button className='btn-modal' onClick={() => setModalIsOpen(false)}>
								Close
							</button>
							<button className='btn-modal' onClick={editTask.bind(taskSave.id)}>
								Edit
							</button>
						</div>
					</div>
				</Modal>
			</div>

	);
}

export default TodoList;
