import '../styles/Note.css';
import addNoteGrid from './AddNoteGrid';


function Note() {
    return (
        <div id="note-container">
            <input id="title-note" type="text" placeholder="Enter title" />
            <textarea id='text-note' placeholder="Your note..."></textarea>
            <button onClick={<addNoteGrid />} id="submit"> Add</button>
        </div>
    )
}


export default Note;
